<?phpnamespace Nemundo\Core\Command;use Nemundo\Core\Debug\Debug;abstract class AbstractLocalCommand extends AbstractCommand{    /**     * @var bool     */    public $showOutput = false;    public function runCommand()    {        $newCommand = $this->getChainedCommand();        exec($newCommand, $output);        if ($this->showOutput) {            (new Debug())->write($output);        }        return $output;    }}