<?phpnamespace Nemundo\Core\DataList;use Nemundo\Core\Base\AbstractBase;class UniqueDataList extends AbstractBase{    /**     * @var bool     */    public $sorted = false;    private $list = [];    public function addValue($value)    {        if (!in_array($value, $this->list)) {            $this->list[] = $value;        }        return $value;    }    public function getCount()    {        $count = sizeof($this->list);        return $count;    }    public function getList()    {        if ($this->sorted) {            asort($this->list);        }        return $this->list;    }}