<?phpnamespace Nemundo\Core\Device;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Debug\Debug;// nach Systemclass DeviceInformation extends AbstractBase{    public function getBrowserAgent()    {        $userAgent = $_SERVER['HTTP_USER_AGENT'];        return $userAgent;    }    public function getIpAddress()    {        $ipAddress = $_SERVER['REMOTE_ADDR'];        return $ipAddress;    }    public function getBrowserLanguage()    {        $language = substr($_SERVER['HTTP_ACCEPT_LANGUAGE'], 0, 2);        return $language;    }    public function getHost() {        $host = $_SERVER['HTTP_HOST'];        return $host;    }    public function getUrl() {        $url = '';        $url = $_SERVER['REQUEST_URI'];        //if (isset($_SERVER['REQUEST_URI']);        return $url;    }    public function getUrlReferer()    {        $urlReferer = '';        if (isset($_SERVER['HTTP_REFERER'])) {            $urlReferer = $_SERVER['HTTP_REFERER'];        }        return $urlReferer;    }    public function getDomainWithScheme() {        /*$result = parse_url($this->url);        $domain = '';        if (isset($result['scheme'])) {            $domain=$result['scheme'].'://';        }        if (isset($result['host'])) {            $domain .=$result['host'];        }*/        //$domain =  parse_url($this->getUrl(), PHP_URL_HOST);        $domain= $_SERVER['REQUEST_SCHEME'].'://'.$_SERVER['HTTP_HOST'];        /*(new Debug())->write($_SERVER);        (new Debug())->write($domain);*/        return $domain;    }}