<?phpnamespace Nemundo\Core\Json\Document;use Nemundo\Core\Http\Response\ResponseType;use Nemundo\Core\Http\Response\HttpResponse;// AbstractDocumentclass JsonResponse extends AbstractJson{    public $filename;    public function render()    {        $response = new HttpResponse();        $response->contentType = ResponseType::JSON;        $response->content = $this->getContent();        $response->attachmentFilename=$this->filename;        $response->sendResponse();    }}