<?phpnamespace Nemundo\Core\Json\Response;use Nemundo\Core\Http\Response\HttpResponse;use Nemundo\Core\Http\Response\ResponseType;use Nemundo\Core\Json\Document\AbstractJson;// AbstractDocumentabstract class AbstractJsonResponse extends AbstractJson{    protected $filename;    public function render()    {        $response = new HttpResponse();        $response->contentType = ResponseType::JSON;        $response->content = $this->getContent();        $response->attachmentFilename=$this->filename;        $response->sendResponse();    }}