<?phpnamespace Nemundo\Core\Language;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Debug\Debug;use Nemundo\Core\Http\Url\UrlRedirect;use Nemundo\Web\WebConfig;class LanguageConfig extends AbstractBase{    /**     * @var bool     */    public static $multiLanguage = false;    /**     * @var string     */    public static $defaultLanguageCode = LanguageCode::EN;    /**     * @var string     */    public static $currentLanguageCode = LanguageCode::DE;  //::EN;    public static $languageList = [];  //   = [LanguageCode::EN,LanguageCode::DE];    public function addLanguage($languageCode)    {        LanguageConfig::$languageList[$languageCode] = $languageCode;        return $this;    }    public function getLanguageList()    {        return LanguageConfig::$languageList;    }    public function setDefaultLanguage($languageCode)    {        LanguageConfig::$defaultLanguageCode = $languageCode;        return $this;    }    public function setCurrentLanguage($languageCode)    {        //$found=false;        if (!in_array($languageCode, LanguageConfig::$languageList)) {            (new Debug())->write('No Language');            exit;            $url = WebConfig::$webUrl . LanguageConfig::$defaultLanguageCode;            (new UrlRedirect())->redirect($url);            //$languageCode= LanguageConfig::$defaultLanguageCode;        }        /*foreach ($this->getLanguageList() as $code) {            if ($code=== $languageCode) {            }        }*/        LanguageConfig::$currentLanguageCode = $languageCode;        return $this;    }}