<?phpnamespace Nemundo\Core\Language;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Debug\Debug;use Nemundo\Core\Http\Url\UrlRedirect;class LanguageConfig extends AbstractBase{    /**     * @var bool     */    public static $multiLanguage = false;    /**     * @var string     */    public static $defaultLanguageCode = LanguageCode::EN;    /**     * @var string     */    public static $currentLanguageCode = LanguageCode::DE;  //::EN;    public static $languageList = [];    public function addLanguage($languageCode)    {        LanguageConfig::$languageList[$languageCode] = $languageCode;        return $this;    }    public function getLanguageList()    {        return LanguageConfig::$languageList;    }    public function setDefaultLanguage($languageCode)    {        LanguageConfig::$defaultLanguageCode = $languageCode;        return $this;    }    public function setCurrentLanguage($languageCode)    {        /*if (!in_array($languageCode, LanguageConfig::$languageList)) {            $url = WebConfig::$webUrl . LanguageConfig::$defaultLanguageCode;            (new UrlRedirect())->redirect($url);        }*/        LanguageConfig::$currentLanguageCode = $languageCode;        return $this;    }    public function getCurrentLanguage()    {        return LanguageConfig::$currentLanguageCode;    }}