<?phpnamespace Nemundo\Core\Text;use Nemundo\Core\Base\AbstractBase;class ByteOrderMarkText extends AbstractBase{    public function removeByteOrderMark($text)    {        if ($text !== null) {            if (substr($text, 0, 3) == chr(hexdec('EF')) . chr(hexdec('BB')) . chr(hexdec('BF'))) {                $text = substr($text, 3);                //return substr($text, 3);            } /*else {                return $text;            }*/        }        return $text;    }}