<?phpnamespace Nemundo\Core\WebRequest;use Nemundo\Core\Debug\Debug;use Nemundo\Core\Log\LogFile;use Nemundo\Core\Log\LogMessage;use Nemundo\Core\Path\Path;use Nemundo\Core\System\Delay;use Nemundo\Core\File\File;class ProxyCurlWebRequest extends CurlWebRequest{    /**     * @var bool     */    //public $proxy = false;    /**     * @var string     */    public $proxyHost;    /**     * @var string     */    public $proxyPort;    /**     * @var string     */    public $proxyUser;    /**     * @var string     */    public $proxyPassword;    protected function load($url)    {        parent::load($url);        if (!$this->loaded) {            $proxyUrl = $this->proxyHost . ':' . $this->proxyPort;            curl_setopt($this->curl, CURLOPT_PROXY, $proxyUrl);            $proxyLogin = $this->proxyUser . ':' . $this->proxyPassword;            curl_setopt($this->curl, CURLOPT_PROXYUSERPWD, $proxyLogin);        }    }}