<?phpnamespace Nemundo\Core\Xml\Reader;use Nemundo\Core\Base\DataSource\AbstractDataSource;use Nemundo\Core\WebRequest\CurlWebRequest;class XmlReader extends AbstractDataSource{    public $prefix = '';    public function fromText($xmlText)    {        $simpleXml = simplexml_load_string($xmlText);        //$xml = simplexml_load_string($xmlText, 'SimpleXMLElement', LIBXML_NOCDATA);        //$xml = simplexml_load_file($filename, 'SimpleXMLElement', LIBXML_NOCDATA, $this->prefix, true);        $this->fromSimpleXml($simpleXml);        return $this;    }    public function fromFilename($filename)    {        $simpleXml = simplexml_load_file($filename);  //, $this->prefix, true);        //$xml = simplexml_load_file($filename, 'SimpleXMLElement', LIBXML_NOCDATA, $this->prefix, true);        $this->fromSimpleXml($simpleXml);        return $this;    }    public function fromUrl($url)    {        $request = new CurlWebRequest();        $xml = $request->getUrl($url)->html;        $this->fromText($xml);    }    private function fromSimpleXml($simpleXml)    {        $json = json_encode($simpleXml);        $xmlNew = json_decode($json, true);        $this->addItem($xmlNew);    }    protected function loadData()    {    }}