<?phprequire __DIR__.'/../config.php';$dayReader = new \Nemundo\Core\Date\DayReader();$dayReader->dateFrom = (new \Nemundo\Core\Type\DateTime\Date())->setNow()->minusDay(5);$dayReader->dateTo = (new \Nemundo\Core\Type\DateTime\Date())->setNow();(new \Nemundo\Core\Debug\Debug())->write('Date From: '.$dayReader->dateFrom->getIsoDate());(new \Nemundo\Core\Debug\Debug())->write('Date To: '.$dayReader->dateTo->getIsoDate());foreach ($dayReader->getData() as $date) {    (new \Nemundo\Core\Debug\Debug())->write($date->getIsoDate());}(new \Nemundo\Core\Debug\Debug())->write('Date From: '.$dayReader->dateFrom->getIsoDate());(new \Nemundo\Core\Debug\Debug())->write('Date To: '.$dayReader->dateTo->getIsoDate());